// App.js
import React, { useState, useEffect } from 'react';
import { Route, Routes, Link, BrowserRouter } from 'react-router-dom';
import './App.css';
import DietInsufficient from './DietInsufficient';
import DietNormal from './DietNormal';
import DietOverweight1 from './DietOverweight1';
import DietOverweight2 from './DietOverweight2';
import DietObesity1 from './DietObesity1';
import DietObesity2 from './DietObesity2';
import DietObesity3 from './DietObesity3';

function App() {
    const [formData, setFormData] = useState({
        gender: '',
        age: '',
        height: '',
        weight: '',
        family_history_with_overweight: '',
        FAVC: '',
        FCVC: '',
        NCP: '',
        CAEC: '',
        SMOKE: '',
        CH2O: '',
        FAF: '',
        TUE: '',
        CALC: '',
        MTRANS: '',
    });

    const [fact, setFact] = useState('');
    const [prediction, setPrediction] = useState(null);

    const facts = [
        "It takes the brain 20 minutes to sense that a person is full.",
        "Women with post-traumatic stress disorder are more likely to be overweight or obese.",
        "One in three U.S. children and teens are overweight or obese.",
        "Childhood obesity affects self-esteem, which can affect employment and higher education later in life.",
        "Obesity prevalence was highest among U.S. adults with a high school diploma or some college education."
    ];

    useEffect(() => {
        const interval = setInterval(() => {
            const randomFact = facts[Math.floor(Math.random() * facts.length)];
            setFact(randomFact);
        }, 5000);
        return () => clearInterval(interval);
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:5000/predict', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const result = await response.json();
            const predictionValue = result.prediction;
            const predictionText = getPredictionText(predictionValue);
            setPrediction({ text: predictionText, value: predictionValue });
        } catch (error) {
            console.error('Error during fetch:', error);
        }
    };

    const getPredictionText = (value) => {
        switch (value) {
            case 0: return 'Insufficient weight';
            case 1: return 'Normal weight';
            case 2: return 'Overweight Level 1';
            case 3: return 'Overweight Level 2';
            case 4: return 'Obesity Level 1';
            case 5: return 'Obesity Level 2';
            case 6: return 'Obesity Level 3';
            default: return 'Unknown';
        }
    };

    return (
        <BrowserRouter>
            <div className="App">
                <header className="App-header">
                    <h1>Obesity Risk Prediction</h1>
                </header>
                <Routes>
                    <Route 
                        path="/" 
                        element={
                            <div>
                                <form onSubmit={handleSubmit}>
                                    <div style={{ display: 'flex', justifyContent: 'center' }}>
                                        <div>
                                            {/* Form Fields */}
                                            <label>Gender:</label>
                                            <select name="gender" value={formData.gender} onChange={handleChange} required>
                                                <option value="">Select</option>
                                                <option value="male">Male</option>
                                                <option value="female">Female</option>
                                                <option value="other">Other</option>
                                            </select>
                                            <label>Age:</label>
                                            <input type="number" name="age" value={formData.age} onChange={handleChange} min="0" required />
                                            <label>Height (meters):</label>
                                            <input type="number" step="0.01" name="height" value={formData.height} onChange={handleChange} min="0" required />
                                            <label>Weight (kg):</label>
                                            <input type="number" step="0.1" name="weight" value={formData.weight} onChange={handleChange} min="0" required />
                                            <label>Family History with Overweight:</label>
                                            <select name="family_history_with_overweight" value={formData.family_history_with_overweight} onChange={handleChange} required>
                                                <option value="">Select</option>
                                                <option value="1">Yes</option>
                                                <option value="0">No</option>
                                            </select>
                                            <label>FAVC:</label>
                                            <select name="FAVC" value={formData.FAVC} onChange={handleChange} required>
                                                <option value="">Select</option>
                                                <option value="1">Yes</option>
                                                <option value="0">No</option>
                                            </select>
                                            <label>FCVC:</label>
                                            <input type="number" name="FCVC" value={formData.FCVC} onChange={handleChange} min="0" required />
                                            <label>NCP:</label>
                                            <input type="number" name="NCP" value={formData.NCP} onChange={handleChange} min="0" required />
                                            <label>CAEC:</label>
                                            <select name="CAEC" value={formData.CAEC} onChange={handleChange} required>
                                                <option value="">Select</option>
                                                <option value="1">Yes</option>
                                                <option value="0">No</option>
                                            </select>
                                            <label>SMOKE:</label>
                                            <select name="SMOKE" value={formData.SMOKE} onChange={handleChange} required>
                                                <option value="">Select</option>
                                                <option value="1">Yes</option>
                                                <option value="0">No</option>
                                            </select>
                                            <label>CH2O:</label>
                                            <input type="number" name="CH2O" value={formData.CH2O} onChange={handleChange} min="0" required />
                                            <label>FAF:</label>
                                            <input type="number" name="FAF" value={formData.FAF} onChange={handleChange} min="0" required />
                                            <label>TUE:</label>
                                            <input type="number" name="TUE" value={formData.TUE} onChange={handleChange} min="0" required />
                                            <label>CALC:</label>
                                            <select name="CALC" value={formData.CALC} onChange={handleChange} required>
                                                <option value="">Select</option>
                                                <option value="1">Yes</option>
                                                <option value="0">No</option>
                                            </select>
                                            <label>MTRANS:</label>
                                            <select name="MTRANS" value={formData.MTRANS} onChange={handleChange} required>
                                                <option value="">Select</option>
                                                <option value="1">Yes</option>
                                                <option value="0">No</option>
                                            </select>
                                            <button type="submit">Submit</button>
                                        </div>
                                    </div>
                                </form>
                                {prediction && (
                                    <div style={{ marginTop: '20px' }}>
                                        <p>Prediction: {prediction.text}</p>
                                        <Link 
                                            to={`/diet-${prediction.text.replace(/\s+/g, '').toLowerCase()}`} 
                                            target="_blank" 
                                            rel="noopener noreferrer"
                                        >
                                            View Dietary Recommendations
                                        </Link>
                                    </div>
                                )}
                                <p style={{ fontStyle: 'italic' }}>Health Fact: {fact}</p>
                            </div>
                        } 
                    />
                    <Route path="/diet-insufficientweight" element={<DietInsufficient />} />
                    <Route path="/diet-normalweight" element={<DietNormal />} />
                    <Route path="/diet-overweightlevel1" element={<DietOverweight1 />} />
                    <Route path="/diet-overweightlevel2" element={<DietOverweight2 />} />
                    <Route path="/diet-obesitylevel1" element={<DietObesity1 />} />
                    <Route path="/diet-obesitylevel2" element={<DietObesity2 />} />
                    <Route path="/diet-obesitylevel3" element={<DietObesity3 />} />
                </Routes>
            </div>
        </BrowserRouter>
    );
}

export default App;
